import React from "react";
// TODO: import useFormik from formik library
import {useFormik} from 'formik';

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      console.log(values);
      alert('Login Successful');
    },
    validate: values => {
      let errors = {};
      if (!values.email) errors.email = 'Field required';
      const regex=/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
      if (!regex.test(values.email)) errors.email = 'Username should be an email';
      if (!values.password) errors.password = 'Field required';
      return errors;
    },
    validateOnChange: false,
    validateOnBlur: false
  });

  return (
    <div className="form-container">

      <h1>My Formik Form</h1>
      <form onSubmit={formik.handleSubmit}>

        <label>Email:</label>
        <input id="emailField" type="text" onChange={formik.handleChange} value={formik.values.email}/>
        {formik.errors.email ? <div id="emailError" style={{color: 'red'}}>{formik.errors.email}</div> : null}

        <label>Password:</label>
        <input id="pswField" type="text" onChange={formik.handleChange} value={formik.values.password}/>
        {formik.errors.password ? <div id="pswError" style={{color: 'red'}}>{formik.errors.password}</div> : ''}

        <button id="submitBtn" type="submit">Submit</button>

      </form>
    </div>
  );
}

export default App;
